mapper.select:
select count(*) as shu
from doc_directory
where 1=1

	AND ID = #{id,jdbcType=VARCHAR}


	AND MingCheng like #{mingCheng,jdbcType=VARCHAR}


	AND xueqi= #{xueqi,jdbcType=VARCHAR}


	AND creator= #{creator,jdbcType=VARCHAR}

select t.* ,(SELECT count(*) from doc_File F  LEFT JOIN doc_directory on doc_directory.id=f.directoryID where f.directoryid= t.id) as countshu,(SELECT count(1) FROM sys_course_user sc LEFT JOIN doc_directory ON sc.courseId=doc_directory.courseId WHERE sc.courseId=t.courseId ) as zong
FROM doc_directory t
WHERE 1=1

	AND id = #{id,jdbcType=VARCHAR}


	AND mingcheng like #{mingCheng,jdbcType=VARCHAR}


	AND xueqi= #{xueqi,jdbcType=VARCHAR}


	AND creator= #{creator,jdbcType=VARCHAR}

limit #{begin}, #{PageSize}

select count(*) as shu
from doc_directory
where 1=1

	AND ID = #{id,jdbcType=VARCHAR}


	AND MingCheng like #{mingCheng,jdbcType=VARCHAR}


	AND xueqi= #{xueqi,jdbcType=VARCHAR}


	AND creator= #{creator,jdbcType=VARCHAR}

select *
	FROM doc_directory d INNER JOIN  sys_course_user c ON c.courseID=d.courseId 
	WHERE 1=1

		AND id = #{id,jdbcType=VARCHAR}


		AND mingcheng like #{mingCheng,jdbcType=VARCHAR}


		AND d.xueqi= #{xueqi,jdbcType=VARCHAR}


		AND c.userId= #{creator,jdbcType=VARCHAR}

	limit #{begin}, #{PageSize}
